frappe.ui.form.on("Attendance", {
"employee": function(frm) {
		if(frm.doc.employee) {
			return frappe.call({
				method: "erpnext.hr.doctype.leave_application.leave_application.get_leave_balance_on",
				args: {
					employee: frm.doc.employee,
					date: frm.doc.att_date,
					leave_type: 'Sick Leave',
					consider_all_leaves_in_the_allocation_period: true
				},
				callback: function(r) {
					if (!r.exc && r.message) {
						frm.set_value('sick_days_remaining', r.message);
					}
				}
			});
		}
		//else(frm.set_value('sick_days_remaining')==='');
}
});
frappe.ui.form.on("Attendance", {
"status": function(frm) {
        if (frm.doc.status=='Present') {
          (frm.set_value('reason', ''));
          (frm.set_value('reason_for_absence', ''));
          (frm.toggle_reqd('hours', true));
          (frm.toggle_reqd('reason', false));
        }
        else if (frm.doc.status=='Absent') {
          (frm.set_value('hours', ''));
          (frm.toggle_reqd('hours', false));
          (frm.toggle_reqd('reason', true));
        }
        else if (frm.doc.status=='Half Day') {
          (frm.toggle_reqd('hours', true));
          (frm.toggle_reqd('reason', true));
        }
        else {
          (frm.toggle_reqd('hours', false));
          (frm.toggle_reqd('reason', false));
          (frm.set_value('hours', ''));
          (frm.set_value('reason', ''));
          (frm.set_value('reason_for_absence', ''));
        }
      }
});

